<?phpnamespace api\models;use Yii;/** * This is the model class for table "site_gallery". * * @property int $id * @property string $photo * @property int $connect_id * @property int $type 1 - Doctor | 2 - Enterprise | 3 - News * @property int $main    0 - Regular | 1 - Main */class SiteDoctorFilesModel extends \yii\db\ActiveRecord{    /**     * {@inheritdoc}     */    public static function tableName()    {        return 'site_doctor_files';    }    /**     * {@inheritdoc}     */    public static function getFiles($connect_id,$type=1)    {        $files = Yii::$app->db->createCommand("SELECT `id`,`file_photo` FROM `site_doctor_files` WHERE `type`=$type and `connect_id`=$connect_id ORDER BY `id` DESC")->queryAll();        return $files;    }    public function rules()    {        return [            [['file_photo', 'connect_id', 'type',], 'required'],            [['connect_id', 'type'], 'integer'],            [['file_photo'], 'string', 'max' => 65]        ];    }    /**     * {@inheritdoc}     */    public function attributeLabels()    {        return [            'id' => 'ID',            'file_photo' => 'Foto',            'connect_id' => 'Connect ID',            'type' => 'Tip'        ];    }}