<?phpnamespace api\models;use Yii;/** * This is the model class for table "site_phone_numbers". * * @property int $id * @property int $connect_id * @property string $number * @property int $number_type 1 - Telefon | 2 - Mobil | 3- Whatsapp * @property int $type 1 - Doctor | 2 - Enterprise * @property string $prefix */class SitePhoneNumbers extends \yii\db\ActiveRecord{    /**     * {@inheritdoc}     */    public static function tableName()    {        return 'site_phone_numbers';    }    /**     * {@inheritdoc}     */    public function rules()    {        return [            [['connect_id', 'number_type', 'type'], 'required'],            [['connect_id', 'number_type', 'type'], 'integer'],            [['number'], 'string', 'max' => 12],        ];    }    public function CheckPhoneNumber($attribute)    {        $type =1;        $field = $this->$attribute;        if(!empty($field[$type]['number']) && $field[$type]['type'] == 1)        {            $strlen = strlen($field[$type]['number']);            if($strlen != 12){                $this->addError($attribute,'Nömrə düzgün formatda deyil');            }        }else{            $this->addError($attribute,'Mobil telefon xanasını boş buraxmayın');        }    }    /**     * {@inheritdoc}     */    public function attributeLabels()    {        return [            'id' => 'ID',            'connect_id' => 'Connect ID',            'number' => 'Number',            'number_type' => 'Number Type',            'type' => 'Type',        ];    }}