<?phpnamespace api\models;use Yii;/** * This is the model class for table "site_promotions". * * @property int $id * @property string $name * @property string $text1 * @property string $text2 * @property string $text3 * @property string $url * @property int $status */class SiteSlider extends \yii\db\ActiveRecord{    public $deletedImages;    public static function tableName()    {        return 'site_slider';    }    /**     * {@inheritdoc}     */    public function rules()    {        return [            [['name','status'], 'required'],            [['url','text1', 'text2','text3','photo','name','deletedImages'], 'string'],            ['status','integer'],            ['photo', 'file','skipOnEmpty' => true, 'extensions'=>'png,jpg,jpeg','maxFiles' => 30,'wrongExtension'=>'Yalnız {extensions}']        ];    }    /**     * Get status     */    public static function get_Status()    {        return [            0 => 'DeAktiv',            1 => 'Aktiv'        ];    }    /**     * {@inheritdoc}     */    public function attributeLabels()    {        return [            'id' => 'ID',            'name' => 'Başlıq',            'photo' => 'Şəkil',            'text1' => 'Mətn 1',            'text2' => 'Mətn 2',            'text3' => 'Mətn 3',            'url' => 'Link',            'status' => 'Status'        ];    }}