<?phpnamespace api\models;use Yii;/** * This is the model class for table "site_promotions". * * @property int $id * @property string $promocode * @property int $promotion_id * @property int $user_id */class SiteUsedPromocode extends \yii\db\ActiveRecord{    public static function tableName()    {        return 'site_used_promotions';    }    /**     * {@inheritdoc}     */    public function rules()    {        return [            [['promocode','promotion_id', 'user_id'], 'required'],            [['promocode'], 'string','max' => 20],        ];    }}