<?phpnamespace backend\controllers;use Yii;use backend\models\SiteConsultation;use backend\models\SiteConsultationSearch;use yii\web\Controller;use yii\web\NotFoundHttpException;use yii\filters\VerbFilter;use backend\controllers\MainController;/** * ConsultationController implements the CRUD actions for SiteConsultation model. */class ConsultationController extends MainController{    const STATUS_ACTIVE = 1;    const STATUS_DEACTIVE = 0;    /**     * {@inheritdoc}     */    public function behaviors()    {        return [            'verbs' => [                'class' => VerbFilter::className(),                'actions' => [                    'delete' => ['POST'],                    'deletemore'=>['POST'],                    'status' => ['POST']                ],            ],        ];    }    /**     * Lists all SiteConsultation models.     * @return mixed     */    public function actionIndex()    {        $searchModel = new SiteConsultationSearch();        $dataProvider = $searchModel->search(Yii::$app->request->queryParams);        return $this->render('index', [            'searchModel' => $searchModel,            'dataProvider' => $dataProvider,        ]);    }    /**     * Displays a single SiteConsultation model.     * @param integer $id     * @return mixed     * @throws NotFoundHttpException if the model cannot be found     */    public function actionView($id)    {        return $this->render('view', [            'model' => $this->findModel($id),        ]);    }    /**     * Creates a new SiteConsultation model.     * If creation is successful, the browser will be redirected to the 'view' page.     * @return mixed     */    public function actionCreate()    {        $model = new SiteConsultation();        if ($model->load(Yii::$app->request->post()) && $model->save()) {            return $this->redirect(['view', 'id' => $model->id]);        }        return $this->render('create', [            'model' => $model,        ]);    }    /**     * Updates an existing SiteConsultation model.     * If update is successful, the browser will be redirected to the 'view' page.     * @param integer $id     * @return mixed     * @throws NotFoundHttpException if the model cannot be found     */    public function actionUpdate($id)    {        $model    = $this->findModel($id);        $oldModel = $this->findModel($id);        if($model->load(Yii::$app->request->post()))        {            if(empty($oldModel->answer) && !empty($model->answer))            {                $model->a_datetime = Yii::$app->params['current.date'].' '.Yii::$app->params['current.time'];            }            if($model->save())            {                return $this->redirect(['index']);            }        }        return $this->render('update', [            'model' => $model,        ]);    }    /**     * Deletes an existing SiteConsultation model.     * If deletion is successful, the browser will be redirected to the 'index' page.     * @param integer $id     * @return mixed     * @throws NotFoundHttpException if the model cannot be found     */    public function actionDelete($id)    {        $this->findModel($id)->delete();        return $this->redirect(['index']);    }    public function actionStatus()    {        $id = intval(Yii::$app->request->post('id'));        $model = $this->findModel($id);        if(trim(Yii::$app->request->post('type'))=='a'){            $model->a_status = $model->a_status == self::STATUS_ACTIVE ? self::STATUS_DEACTIVE : self::STATUS_ACTIVE;        }else{            $model->status = $model->status == self::STATUS_ACTIVE ? self::STATUS_DEACTIVE : self::STATUS_ACTIVE;        }        if($model->save(false))        {            Yii::$app->session->setFlash('success','Status dəyişildi');        }else{            Yii::$app->session->setFlash('error','Xəta baş verdi');        }        return $this->redirect(Yii::$app->request->referrer ?: Yii::$app->homeUrl);    }    /**     * Finds the SiteConsultation model based on its primary key value.     * If the model is not found, a 404 HTTP exception will be thrown.     * @param integer $id     * @return SiteConsultation the loaded model     * @throws NotFoundHttpException if the model cannot be found     */    protected function findModel($id)    {        if (($model = SiteConsultation::findOne($id)) !== null) {            return $model;        }        throw new NotFoundHttpException('The requested page does not exist.');    }}