<?phpnamespace backend\controllers;use Yii;use yii\filters\AccessControl;use backend\models\SitePackagesTypes;use backend\models\SitePackagesTypesSearch;use yii\web\Controller;use yii\web\NotFoundHttpException;use yii\filters\VerbFilter;use backend\controllers\MainController;/** * PackagesTypesController implements the CRUD actions for SitePackagesTypes model. */class PackagesTypesController extends MainController{    /**     * {@inheritdoc}     */    public $customPath = 'packages-types';    public function behaviors()    {        return [            'access' => [                'class' => AccessControl::className(),                'only' => ['*'],                'rules' => [                    [                        'allow' => true,                        'roles' => ['@'],                    ],                ],            ],            'verbs' => [                'class' => VerbFilter::className(),                'actions' => [                    'delete' => ['POST'],                ],            ],        ];    }    /**     * Lists all SitePackagesTypes models.     * @return mixed     */    public function actionIndex()    {        $searchModel = new SitePackagesTypesSearch();        $dataProvider = $searchModel->search(Yii::$app->request->queryParams);        return $this->render('index', [            'searchModel' => $searchModel,            'dataProvider' => $dataProvider,            'customPath' => $this->customPath,        ]);    }    /**     * Displays a single SitePackagesTypes model.     * @param integer $id     * @return mixed     * @throws NotFoundHttpException if the model cannot be found     */    public function actionView($id)    {        return $this->render('view', [            'model' => $this->findModel($id),        ]);    }    /**     * Creates a new SitePackagesTypes model.     * If creation is successful, the browser will be redirected to the 'view' page.     * @return mixed     */    public function actionCreate()    {        $model = new SitePackagesTypes();        if ($model->load(Yii::$app->request->post()) && $model->save()) {            return $this->redirect(['index']);        }        return $this->render('create', [            'model' => $model,        ]);    }    /**     * Updates an existing SitePackagesTypes model.     * If update is successful, the browser will be redirected to the 'view' page.     * @param integer $id     * @return mixed     * @throws NotFoundHttpException if the model cannot be found     */    public function actionUpdate($id)    {        $model = $this->findModel($id);        if ($model->load(Yii::$app->request->post()) && $model->save()) {            return $this->redirect(['index']);        }        return $this->render('update', [            'model' => $model,        ]);    }    /**     * Deletes an existing SitePackagesTypes model.     * If deletion is successful, the browser will be redirected to the 'index' page.     * @param integer $id     * @return mixed     * @throws NotFoundHttpException if the model cannot be found     */    public function actionDelete($id)    {        $this->findModel($id)->delete();        return $this->redirect(['index']);    }    public function actionDeletemore()    {        $ids = Yii::$app->request->post('del_check');        if(!empty($ids))        {            foreach ($ids as $id)            {                $this->actionDelete($id);            }            Yii::$app->session->setFlash('success','Məlumatlar silindi.');        }else{            Yii::$app->session->setFlash('error','Heç bir seçim edilməyib.');        }        return $this->redirect(Yii::$app->request->referrer ?: Yii::$app->homeUrl);    }    /**     * Finds the SitePackagesTypes model based on its primary key value.     * If the model is not found, a 404 HTTP exception will be thrown.     * @param integer $id     * @return SitePackagesTypes the loaded model     * @throws NotFoundHttpException if the model cannot be found     */    protected function findModel($id)    {        if (($model = SitePackagesTypes::findOne($id)) !== null) {            return $model;        }        throw new NotFoundHttpException('The requested page does not exist.');    }}