<?phpnamespace backend\models;use Yii;/** * This is the model class for table "site_comments". * * @property int $id * @property int $connect_id * @property string $name * @property string $email * @property string $comment * @property string $datetime * @property int $positive * @property int $status 0 - deactive 1 - active * @property int $type 1 - Doctor | 2 - Enterprise */class SiteComments extends \yii\db\ActiveRecord{//    public $connect_id;//    public $name;//    public $email;//    public $comment;//    public $datetime;//    public $positive;//    public $status;//    public $type;    /**     * {@inheritdoc}     */    public static function tableName()    {        return 'site_comments';    }    public static function getType()    {        return [            1 => 'Həkim',            2 => 'Obyekt',            3 => 'Xəbər'        ];    }    public function getClassName()    {        $exp = explode('\\',__CLASS__);        return $exp[count($exp)-1];    }    /**     * {@inheritdoc}     */    public function rules()    {        return [            [['connect_id', 'name', 'email', 'comment', 'datetime', 'positive', 'status', 'type'], 'required'],            [['connect_id', 'positive', 'status', 'type'], 'integer'],            [['comment'], 'string'],            [['datetime'], 'safe'],            [['name', 'email'], 'string', 'max' => 60],        ];    }    public function checkIsArray($attribute)    {        if(!is_array($this->$attribute)){            $this->addError($attribute,'Xəta! Massiv deyil!');        }    }    /**     * {@inheritdoc}     */    public function attributeLabels()    {        return [            'id' => 'ID',            'connect_id' => 'Əlaqədar',            'name' => 'Ad',            'email' => 'Email',            'comment' => 'Komment',            'datetime' => 'Tarix',            'positive' => 'Tövsiyyə Edilən',            'status' => 'Status',            'type' => 'Tip',        ];    }}