<?phpnamespace backend\models;use Yii;/** * This is the model class for table "site_packages_types". * * @property int $id * @property string $name * @property int $type */class SitePackagesTypes extends \yii\db\ActiveRecord{    /**      * {@inheritdoc}     */    public static function tableName()    {        return 'site_packages_types';    }    public static function getPackage($id)    {   $db = Yii::$app->db;        $connected = $db->createCommand("SELECT `name` FROM site_packages WHERE id='$id'")->queryOne();        //return print_r($connected);        if(!empty($connected))        {            return $connected['name'];        }        return 'Yoxdur';    }    public static function get_Type()    {        return [            0 => 'Psixoloq',            1 => 'Dietoloq'        ];    }    /**     * {@inheritdoc}     */    public function rules()    {        return [            [['name'], 'required'],            [['type'], 'integer'],            [['name'], 'string', 'max' => 255],        ];    }    /**     * {@inheritdoc}     */    public function attributeLabels()    {        return [            'id' => 'ID',            'name' => 'Mövzu Adı',            'type' => 'Tip',        ];    }}