<?phpnamespace backend\models;use Yii;/** * This is the model class for table "site_phone_numbers". * * @property int $id * @property int $connect_id * @property string $number * @property int $number_type 1 - Telefon | 2 - Mobil | 3- Whatsapp * @property int $type 1 - Doctor | 2 - Enterprise * @property string $prefix */class SitePhoneNumbers extends \yii\db\ActiveRecord{    /**     * {@inheritdoc}     */    public static function tableName()    {        return 'site_phone_numbers';    }    /**     * {@inheritdoc}     */    public function rules()    {        return [            [['connect_id', 'number', 'number_type', 'type'], 'required'],            [['connect_id', 'number_type', 'type'], 'integer'],            [['number'], 'string', 'max' => 60],        ];    }    /**     * {@inheritdoc}     */    public function attributeLabels()    {        return [            'id' => 'ID',            'connect_id' => 'Connect ID',            'number' => 'Number',            'number_type' => 'Number Type',            'type' => 'Type',        ];    }}