<?phpuse yii\widgets\ActiveForm;$template = ['template' => "{label}{input}{hint}<div class=\"help-block help-block-error\">{error}</div>"];$form = ActiveForm::begin(['enableClientScript' => false,    'action' => ['auth/login2'],    'options'=>[        'enctype' => 'multipart/form-data',        'id' => "login_form_modal",    ]]);?>        <h4>E-poçt:</h4>        <div class="input-group">            <label>                <?= $form->field($model, 'email',$template)->textInput(['class'=>'form-control','placeholder'=>'İşlək e-poçt ünvanınızı qeyd edin'])->label(false); ?>            </label>        </div>        <h4>Şifrə:</h4>        <div class="input-group">            <label>                <?= $form->field($model, 'password',$template)->passwordInput(['class'=>'form-control','placeholder'=>'Şifrə'])->label(false); ?>            </label>        </div>        <div class="forget-password">            <label class="check-button">                <span class="check-btn-text">Yadda saxla</span>                <input type="checkbox" id="loginform-rememberme" name="LoginForm[rememberMe]" value="1">                <span class="checkmark"></span>            </label>            <span class="right-item"><a href="<?= \yii\helpers\Url::to(["site/request-password-reset"])?>" >Şifrəni unutmusunuz?</a></span>        </div><?phpActiveForm::end();